Shape.prototype.destroy = function() {
	var parentShape = this.parentShape;
	if(parentShape) {
		if(parentShape.targetShape === this) {
			parentShape.targetShape = null;	
		}
		if(parentShape.pointerEventTarget === this) {
			parentShape.pointerEventTarget = null;
		}
		this.parentShape = null;	
	}

	if(this.container) {
		this.container = null;
	}

	this.app = null;
	this.view = null;

	if(this.children) {
		this.children.clear(true);
		this.children = null;
	}

	if(this.images) {
		this.images = null;
	}

	if(this.events) {
		this.events = null;
	}

	if(this.style) {
		this.style = null;
	}

	this.onDestroy();
	this.isInvalid = true;
	this.jsonData = null;

	return;
}


